import React from "react";
import { motion } from "framer-motion";

function About() {



    return(
        <div>
            {/* About Section */}
            <section id="about" className="py-20 container mx-auto px-6">
            <motion.h2 initial={{ opacity: 0, y: 40 }} whileInView={{ opacity: 1, y: 0 }} transition={{ duration: 0.8 }} viewport={{ once: true }} className="text-3xl font-bold text-center mb-12">
            About Me
            </motion.h2>
            <div className="grid md:grid-cols-2 gap-12 items-center">
            <motion.img src="https://via.placeholder.com/400" alt="Profile" className="rounded-2xl shadow-lg" initial={{ opacity: 0, x: -50 }} whileInView={{ opacity: 1, x: 0 }} transition={{ duration: 1 }} viewport={{ once: true }}/>
            <motion.p className="text-lg leading-relaxed" initial={{ opacity: 0, x: 50 }} whileInView={{ opacity: 1, x: 0 }} transition={{ duration: 1 }} viewport={{ once: true }}>
                I am an MCA graduate with strong problem-solving skills and a passion for software development. Skilled in Python, Django, MySQL, and modern web technologies. I love creating impactful solutions and continuously improving my craft.
            </motion.p>
            </div>
        </section>
        </div>
    )

}

export default About;
