import React, {useState, useEffect} from "react";
import { motion } from "framer-motion";
import api from '../api';

function About() {

    const [personalInfo, setPersonalInfo] = useState(null);

    useEffect(() => {
        document.documentElement.style.scrollBehavior = "smooth";
        api.get("/personal-info/").then((res)=>{
        if (res.data.length > 0){
            setPersonalInfo(res.data[0]);
        }
        }).catch((err) => console.error(err));
    }, []);

    return(
        <div>
            {/* About Section */}
            <section id="about" className="py-20 container mx-auto px-6">
            <motion.h2 initial={{ opacity: 0, y: 40 }} whileInView={{ opacity: 1, y: 0 }} transition={{ duration: 0.8 }} viewport={{ once: true }} className="text-3xl font-bold text-center mb-12">
            About Me
            </motion.h2>
            <div className="grid md:grid-cols-2 gap-12 items-center">
            <motion.img src={personalInfo.profile_photo} alt="Profile" className="max-w-xs rounded-2xl shadow-lg object-contain" initial={{ opacity: 0, x: -50 }} whileInView={{ opacity: 1, x: 0 }} transition={{ duration: 1 }} viewport={{ once: true }}/>
            <motion.p className="text-lg leading-relaxed" initial={{ opacity: 0, x: 50 }} whileInView={{ opacity: 1, x: 0 }} transition={{ duration: 1 }} viewport={{ once: true }}>
                I am a Backend Developer specializing in Django with expertise in Python, MySQL, and modern web technologies. Recently, I built a Server Performance Monitoring System to track and optimize server efficiency. I focus on writing clean, scalable code and continuously improving my skills to grow into a full-stack developer.
            </motion.p>
            </div>
        </section>
        </div>
    )

}

export default About;
