import React, { useEffect, useState } from "react";
import api from "../api";

const PersonalInfo = () => {
  const [info, setInfo] = useState({});

  // Fetch personal info from Django API
  useEffect(() => {
    api.get("/personal-info/")
      .then((res) => setInfo(res.data[0] || {}));
  }, []);

  return (
    <section className="personal-info">
      <img
        src={`http://localhost:8000${info.profile_photo || ''}`}
        alt="Profile"
        style={{ borderRadius: "100px", width: "180px" }}
      />
      <h1>{info.name}</h1>
      <p className="accent">{info.bio}</p>
      <p>Contact: {info.contact}</p>
      <p>Email: <a href={`mailto:${info.email}`}>{info.email}</a></p>
    </section>
  );
};

export default PersonalInfo;
