import React, { useEffect, useState } from "react";
import api from "../api";

const Projects = () => {
  const [projects, setProjects] = useState([]);

  useEffect(() => {
    api.get("/projects/").then((res) => setProjects(res.data));
  }, []);

  return (
    <section>
      <h2>Projects</h2>
      {projects.map((project) => (
        <div key={project.id} style={{ marginBottom: "20px" }}>
          <h4 className="accent">{project.title}</h4>
          <p>{project.description}</p>
          <em>{project.technologies}</em>
          {project.repo_link && (
            <p>
              <a href={project.repo_link} className="highlight" target="_blank" rel="noopener noreferrer">
                Source Code
              </a>
            </p>
          )}
        </div>
      ))}
    </section>
  );
};

export default Projects;
