// src/App.js
import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import api  from "./api";

// Smooth scroll

function App() {

  const [personalInfo, setPersonalInfo] = useState(null);

  useEffect(() => {
    document.documentElement.style.scrollBehavior = "smooth";
    api.get("/personal-info/").then((res)=>{
      if (res.data.length > 0){
        setPersonalInfo(res.data[0]);
      }
    }).catch((err) => console.error(err));
  }, []);

  if (!personalInfo){
    return <p>Loading ...</p>// simple loading
  }

  return (
    <div className="dark bg-gray-900 text-gray-100 font-sans">
      {/* Navigation */}
      <header className="fixed top-0 left-0 w-full bg-gray-800 shadow z-50">
        <nav className="container mx-auto flex items-center justify-between p-4">
          <div className="text-2xl font-bold text-blue-400">{personalInfo.name}</div>
          <ul className="hidden md:flex space-x-8 font-medium">
            <li><a href="#introduction" className="hover:text-blue-400">Intro</a></li>
            <li><a href="#about" className="hover:text-blue-400">About</a></li>
            <li><a href="#projects" className="hover:text-blue-400">Projects</a></li>
            <li><a href="#contact" className="hover:text-blue-400">Contact</a></li>
          </ul>
        </nav>
      </header>

      {/* Introduction Section */}
      <section
        id="introduction"
        className="min-h-screen flex flex-col justify-center items-center bg-gradient-to-r from-blue-600 to-purple-700 text-white text-center px-6"
      >
        <motion.h1
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1 }}
          className="text-5xl font-extrabold mb-4"
        >
          Hi, I’m {personalInfo.name} 👋
        </motion.h1>
        <motion.p
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.5, duration: 1 }}
          className="text-xl max-w-2xl"
        >
          {personalInfo.bio}
        </motion.p>
        <motion.a
          href="#projects"
          whileHover={{ scale: 1.05 }}
          className="mt-6 px-6 py-3 bg-white text-blue-600 font-semibold rounded-lg shadow hover:bg-gray-100 transition"
        >
          View My Work
        </motion.a>
      </section>

      {/* About Section */}
      <section id="about" className="py-20 container mx-auto px-6">
        <motion.h2
          initial={{ opacity: 0, y: 40 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-3xl font-bold text-center mb-12"
        >
          About Me
        </motion.h2>
        <div className="grid md:grid-cols-2 gap-12 items-center">
          <motion.img
            src="https://via.placeholder.com/400"
            alt="Profile"
            className="rounded-2xl shadow-lg"
            initial={{ opacity: 0, x: -50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 1 }}
            viewport={{ once: true }}
          />
          <motion.p
            className="text-lg leading-relaxed"
            initial={{ opacity: 0, x: 50 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 1 }}
            viewport={{ once: true }}
          >
            I am an MCA graduate with strong problem-solving skills and a passion for
            software development. Skilled in Python, Django, MySQL, and modern web technologies.
            I love creating impactful solutions and continuously improving my craft.
          </motion.p>
        </div>
      </section>

      {/* Projects Section */}
      <section id="projects" className="py-20 bg-gray-800">
        <div className="container mx-auto px-6">
          <motion.h2
            initial={{ opacity: 0, y: 40 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
            viewport={{ once: true }}
            className="text-3xl font-bold text-center mb-12"
          >
            Projects
          </motion.h2>
          <div className="grid md:grid-cols-3 gap-8">
            {[1, 2, 3].map((project) => (
              <motion.div
                key={project}
                className="bg-gray-900 rounded-2xl shadow hover:shadow-lg transition p-6"
                initial={{ opacity: 0, y: 50 }}
                whileInView={{ opacity: 1, y: 0 }}
                transition={{ duration: 0.8, delay: project * 0.2 }}
                viewport={{ once: true }}
              >
                <h3 className="text-xl font-semibold mb-2">Project {project}</h3>
                <p className="text-gray-400">
                  A brief description of the project goes here. Highlight technologies,
                  purpose, and key achievements.
                </p>
                <a
                  href="#"
                  className="mt-4 inline-block text-blue-400 font-medium hover:underline"
                >
                  View Details →
                </a>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Contact Section */}
      <section id="contact" className="py-20 container mx-auto px-6">
        <motion.h2
          initial={{ opacity: 0, y: 40 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
          className="text-3xl font-bold text-center mb-12"
        >
          Get In Touch
        </motion.h2>
        <form className="max-w-xl mx-auto bg-gray-800 shadow rounded-2xl p-8">
          <div className="mb-6">
            <label className="block text-sm font-medium mb-2">Name</label>
            <input
              type="text"
              className="w-full border rounded-lg p-3 bg-gray-900 text-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div className="mb-6">
            <label className="block text-sm font-medium mb-2">Email</label>
            <input
              type="email"
              className="w-full border rounded-lg p-3 bg-gray-900 text-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500"
            />
          </div>
          <div className="mb-6">
            <label className="block text-sm font-medium mb-2">Message</label>
            <textarea
              rows="4"
              className="w-full border rounded-lg p-3 bg-gray-900 text-gray-100 focus:outline-none focus:ring-2 focus:ring-blue-500"
            ></textarea>
          </div>
          <button
            type="submit"
            className="w-full py-3 bg-blue-600 text-white font-semibold rounded-lg hover:bg-blue-700 transition"
          >
            Send Message
          </button>
        </form>
      </section>

      {/* Footer */}
      <footer className="bg-gray-950 text-gray-400 py-6 text-center">
        <p>© {new Date().getFullYear()} Pravin F. All rights reserved.</p>
      </footer>
    </div>
  );
}

export default App;
